安装 deb:
curl -L -O https://artifacts.elastic.co/downloads/beats/filebeat/filebeat-7.3.0-amd64.debsudo dpkg -i filebeat-7.3.0-amd64.debrpm:
curl -L -O https://artifacts.elastic.co/downloads/beats/filebeat/filebeat-7.3.0-x86_64.rpmsudo rpm -vi filebeat-7.3.0-x86_64.rpmdocker-compose version: '2'services:filebeat:image: docker.elastic.co/beats/filebeat:7.2.0container_name: filebeatrestart: alwaysvolumes:- ./config/filebeat.yml:/usr/share/filebeat/filebeat.yml- /var/lib/docker/containers:/var/lib/docker/containersuser: root容器日志配置 {&quot;log-driver&quot;:&quot;json-file&quot;,&quot;log-opts&quot;:{ &quot;max-size&quot; :&quot;5m&quot;,&quot;max-file&quot;:&quot;1&quot;, &quot;labels&quot;:&quot;{{.Name}}&quot;}}
